content:
[ls] :is(button, input, select, textarea), [ls] {
  font-family: var(--font) !important;
  color: var(--color);
}

body, [ls] {
  background: var(--background);
  overflow-y: overlay;
}

[ls-theme=dark], [ls-theme=amoled], [ls-theme=default], body:not([ls-theme]) {
  --background-raw: 25, 25, 25;
  --background: rgb(var(--background-raw));
  --background-tr: #222a;
  --transparent-overlay: #fff1;
  --transparent-overlay-strong: #fff4;
  --transparent-overlay-dark: #00000018;
  --transparent-overlay-dark-strong: #0005;
  --color: var(--elevate-8);
  --ui: var(--bg-6);
  --ui-bg-0: var(--bg-1);
  --ui-bg-1: var(--bg-4);
  --ui-border: var(--border-color-0);
  --ui-overlay: #0c0c0ca3;
  --border-color-0: #0c0c0c;
  --border-color: var(--border-color-0);
  --bg-0: #000;
  --bg-1: #121212;
  --bg-2: #151515;
  --bg-3: var(--background);
  --bg-4: #1c1c1c;
  --bg-5: #202020;
  --bg-6: #232323;
  --bg-7: #272727;
  --bg-8: #333;
  --bg: var(--background);
  --elevate-0: #222;
  --elevate-1: #333;
  --elevate-2: #444;
  --elevate-3: #555;
  --elevate-4: #666;
  --elevate-5: #aaa;
  --elevate-6: #ccc;
  --elevate-7: #ddd;
  --elevate-8: #eee;
  --elevate: var(--elevate-0);
}
[ls-theme=dark] [ls-accent=auto], [ls-theme=amoled] [ls-accent=auto], [ls-theme=default] [ls-accent=auto], body:not([ls-theme]) [ls-accent=auto] {
  --accent-raw: 225, 225, 225;
  --accent-dark-raw: 190, 190, 190;
  --accent-light-raw: 245, 245, 245;
  --accent-text-raw: 26, 26, 26;
  --accent-background-raw: 60, 60, 60;
  --color-bg: #222 ;
}

[ls-theme=amoled] {
  --background-raw: 0, 0, 0;
  --elevate-0: #151515;
  --elevate-1: #222;
  --elevate-2: #333;
  --elevate-3: #444;
  --elevate-4: #585858;
}

[ls-theme=light] {
  --background-raw: 255, 255, 255;
  --background: rgb(var(--background-raw));
  --background-tr: #fffa;
  --transparent-overlay: #0001;
  --transparent-overlay-strong: #0004;
  --transparent-overlay-dark: #0001;
  --transparent-overlay-dark-strong: #0003;
  --color: var(--elevate-8);
  --ui: var(--bg-6);
  --ui-bg-0: var(--bg-1);
  --ui-bg-1: var(--bg-4);
  --ui-border: var(--border-color-0);
  --ui-overlay: #7272725e;
  --border-color-0: #727272;
  --border-color: var(--border-color-0);
  --bg-0: #b4b4b4;
  --bg-1: #e4e4e4;
  --bg-2: #f3f3f3;
  --bg-3: var(--background);
  --bg-4: #f1f1f1;
  --bg-5: #ececec;
  --bg-6: #e4e4e4;
  --bg-7: #dadada;
  --bg-8: #c7c7c7;
  --bg: var(--background);
  --elevate-0: #eee;
  --elevate-1: #ddd;
  --elevate-2: #bbb;
  --elevate-3: #aaa;
  --elevate-4: #888;
  --elevate-5: #666;
  --elevate-6: #555;
  --elevate-7: #444;
  --elevate-8: #222;
  --elevate: var(--elevate-0);
}
[ls-theme=light] [ls-accent=auto] {
  --accent-raw: 21, 21, 21;
  --accent-light-raw: 41, 41, 41;
  --accent-dark-raw: 11, 11, 11;
  --accent-text-raw: 215, 215, 215;
  --accent-background-raw: 120, 120, 120;
  --color-bg: #eee ;
}

[ls] input {
  position: relative;
}
[ls] :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button) {
  cursor: pointer;
}
[ls] :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button) i {
  font-size: 1em;
  margin: 0 4px 0 2px;
}
[ls] :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button) i:only-child {
  margin: 0;
}
[ls] img {
  padding: 0;
}
[ls] table {
  background-color: rgba(255, 255, 255, 0.0666666667);
  border-collapse: collapse;
  border: 1px solid rgba(255, 255, 255, 0.3333333333);
  overflow: hidden;
  border-radius: 15px;
}
[ls] table :is(th, td) {
  border: 1px solid rgba(255, 255, 255, 0.1333333333);
  text-align: center;
  padding: 5px;
}
[ls] a {
  color: var(--accent);
  text-decoration: none;
}
[ls] a[disabled] {
  color: var(--elevate-3);
}
[ls] a:focus:not(.ls-button, .ls-plain) {
  box-shadow: 0 0 0 3px rgba(0, 0, 0, 0.2666666667);
  outline: 3px solid var(--accent);
  outline-offset: 3px;
  border-radius: 3px;
}
[ls] select {
  padding: 0.3125em;
}
[ls] :is(input[type=text], input:not([type=button]):not([type=submit]):not([type=color]):not([type=file]):not([type=reset]):not([type=image])), [ls] textarea {
  color: var(--color) !important;
}
[ls] :is(input[type=text], input:not([type=button]):not([type=submit]):not([type=color]):not([type=file]):not([type=reset]):not([type=image]))::selection, [ls] textarea::selection {
  background-color: rgb(var(--accent-raw), 50%);
}
[ls] h1 {
  font-weight: 800;
}
[ls] h2 {
  font-weight: 600;
}
[ls] h3, [ls] h4 {
  font-weight: 300;
  color: var(--elevate-3);
}
[ls] hr {
  border: 1px solid var(--elevate-1);
  border-radius: 10px;
  margin: var(--hr-spacing, 18px) 0;
}
[ls] :is([disabled], :disabled) {
  cursor: not-allowed;
  opacity: 0.9;
  filter: brightness(0.7);
}
[ls] * {
  box-sizing: border-box;
}
[ls] p, [ls] header, [ls] body, [ls] html {
  margin: 0;
}

:is(:is(button, input[type=button], input[type=submit], input[type=reset], .ls-button), input, textarea) {
  color: var(--color-bg, var(--color));
  border: 0;
  outline: none;
  position: relative;
  background: var(--ui-bg-0);
  font-size: 1em;
}

.ls-checkbox, .ls-radio {
  padding-left: calc(1em + 20px);
  position: relative;
  display: inline-block;
  -webkit-user-select: none;
  user-select: none;
}

.ls-checkbox > input, .ls-radio > input, .ls-radio-button > input {
  opacity: 0;
  width: 0;
  height: 0;
  position: absolute !important;
}

.ls-checkbox > span, .ls-radio > span {
  width: 1.2em;
  height: 1.2em;
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  top: 50%;
  left: 10px;
  transform: translateY(-50%);
}

.ls-checkbox > span::after {
  content: "";
  width: 0.2em;
  height: 0.5em;
  border: solid var(--color-bg);
  border-width: 0 0.15em 0.15em 0;
  -webkit-transform: rotate(45deg);
  transform: rotate(45deg);
  display: none;
}

:is(.ls-checkbox, .ls-radio) > input:checked ~ span::after {
  display: block;
}

:is(.ls-radio-button) > input:checked ~ :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button) {
  background: var(--accent-dark-transparent);
  color: var(--color-bg);
}

.ls-radio > span::after {
  content: "";
  width: 0.55em;
  height: 0.55em;
  background: var(--color-bg);
  display: none;
}

ls-box {
  background: var(--elevate-1);
  display: block;
}

ls-box:not(.contained, .color, .inline) {
  outline: 1px solid var(--transparent-overlay-strong);
  box-shadow: 0 10px 20px 0 rgba(0, 0, 0, 0.1333333333);
}

ls-box.inline {
  display: inline-block;
  border-radius: var(--br-small-inside);
  padding: 0.1em 0.5em;
  margin: 0 0.2em;
}

ls-box.color {
  color: var(--accent-light);
  background: rgb(var(--accent-raw), 10%);
}

ls-box::selection {
  background: var(--accent-transparent);
}

/*
    This function increases specificity x times by repeating the selector.

    eg.

    buttonpriority(".myClass", 2) { ... }

    will result in:

    button.myClass.myClass { ... }

    which boosts the specificity to 0,2,1 instead of 0,1,1 if the class was written just once.

    Use this only in edge cases, where a higher selector is breaking a lower selector and there is not a better way.
*/
[ls] {
  --br: 6px;
  --br-lg: 8px;
  --br-tiny: 5px;
  --br-tiny-inside: 4px;
  --br-small: 8px;
  --br-small-inside: 5px;
  --br-medium: 14px;
  --br-medium-inside: 10px;
  --br-big: 18px;
  --br-big-inside: 15px;
  --br-round: 24px;
  --br-round-inside: 20px;
  --br-extra: 34px;
  --br-extra-inside: 30px;
  --shadow-overlay-0: 0 12px 32px 0 #0004;
  --shadow-overlay-1: 0 12px 24px 0 #0006;
  --shadow-overlay: var(--shadow-overlay-0);
  --shadow-float-0: 0 4px 16px -5px #0008;
  --shadow-float-1: 0 6px 18px -5px #000a;
  --shadow-float-box: 0 10px 20px 0 #0002;
  --shadow-float: var(--shadow-float-0);
  --shadow-elevate-0: 0 1px 4px 0 #0004, inset 0 1px 0 0 #fff2;
  --shadow-elevate-1: 0 2px 8px 0 #0004, inset 0 1px 0 0 #fff3;
  --shadow-elevate-3: 0 4px 12px 4px #0004, inset 0 1px 0 0 #fff4;
  --shadow-elevate: var(--shadow-elevate-0);
}

#ls-top-layer {
  position: fixed;
  z-index: 9000000000000;
}

ls-label {
  margin: 0.2em 0;
  margin-left: 10px;
  font-weight: bold;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  max-width: 100%;
  display: block;
}

:is(button, input[type=button], input[type=submit], input[type=reset], .ls-button).pill, ls-select.pill {
  border-radius: var(--br-round);
  padding: 0.4em 1.2em;
}

:is(button, input[type=button], input[type=submit], input[type=reset], .ls-button).circle {
  border-radius: 50%;
  padding: 0.7em;
  line-height: 0;
}

ls-menu {
  --scrollbar-size: 3px;
  display: none;
  position: fixed;
  z-index: 10000;
  overflow: overlay;
  overflow-x: hidden;
  text-align: left;
  background: var(--ui);
  -webkit-user-select: none;
  user-select: none;
  font-size: 1em;
  min-width: 100px;
}

ls-menu.has-top-handle {
  border-top-left-radius: 0 !important;
  border-top-right-radius: 0 !important;
}

[ls] ls-menu > :is(input[type=text], input:not([type=button]):not([type=submit]):not([type=color]):not([type=file]):not([type=reset]):not([type=image])) {
  margin: 0.5em;
  width: 100%;
  width: -webkit-fill-available;
  width: -moz-fill-available;
  background: var(--ui-bg-1);
}

ls-menu hr {
  --hr-spacing: .5em;
  border-color: rgba(0, 0, 0, 0.1333333333);
}

ls-option.indent {
  padding-left: 1em;
}

ls-option {
  cursor: pointer;
  display: block;
  border-radius: var(--br);
  margin: 2px 5px;
  padding: 1px 8px;
  position: relative;
}

ls-option:hover, ls-option.submenu-visible {
  background: var(--elevate-1);
}

ls-option:active:not(.submenu-visible) {
  background: var(--elevate-2);
}

ls-option i, .ls-select-label i {
  margin-right: 10px;
}

}
[ls-style=flat] :is(textarea):focus, [ls-style=flat] :is(input[type=text], input:not([type=button]):not([type=submit]):not([type=color]):not([type=file]):not([type=reset]):not([type=image])):focus {
  outline: 2px solid var(--accent-focus, var(--accent));
  background: rgb(var(--accent-raw), 10%);
}
[ls-style=flat] :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button) {
  background: var(--accent);
  color: var(--color-bg, #fff);
}
[ls-style=flat] :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button):not(.pill, .circle), [ls-style=flat] input, [ls-style=flat] textarea {
  border-radius: var(--br-small);
  padding: 6px 18px;
}
[ls-style=flat] :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button), [ls-style=flat] input, [ls-style=flat] textarea {
  margin: 4px 0;
}
[ls-style=flat] input, [ls-style=flat] textarea {
  padding: 0.3125em 0.625em;
}
[ls-style=flat] ls-box {
  border-radius: var(--br-small);
  padding: 0.5em 1em;
  margin: 0.4em;
}
[ls-style=flat] :is(:is(button, input[type=button], input[type=submit], input[type=reset], .ls-button), ls-select).elevated {
  outline: none;
  background: rgb(var(--accent-raw), 10%);
  color: var(--accent-light);
  transition-duration: 0.1s;
  transition-property: background;
}
[ls-style=flat] :is(:is(button, input[type=button], input[type=submit], input[type=reset], .ls-button), ls-select).outlined {
  outline: none;
  background: none;
  color: var(--accent-light);
  border: 1px solid var(--accent-light);
  transition-duration: 0.1s;
  transition-property: background;
}
[ls-style=flat] :not(ls-group) > :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button):focus {
  box-shadow: 0 0 0 3px var(--transparent-overlay-dark);
}
[ls-style=flat] :is(:is(button, input[type=button], input[type=submit], input[type=reset], .ls-button), ls-select):focus-visible {
  outline: 2px solid var(--accent-light);
  opacity: 1;
}
[ls-style=flat] :is(:is(button, input[type=button], input[type=submit], input[type=reset], .ls-button), ls-select):not(.elevated, .outlined):focus-visible {
  outline: 2px solid var(--color);
}
[ls-style=flat] :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button):hover:not([disabled]) {
  filter: brightness(1.1);
}
[ls-style=flat] :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button):is(.active, :active):not([disabled]) {
  background: var(--accent-light);
}
[ls-style=flat] :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button).elevated:hover:not([disabled]) {
  background: rgb(var(--accent-raw), 15%);
}
[ls-style=flat] :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button).elevated:active:not([disabled]) {
  background: rgb(var(--accent-raw), 25%);
}
[ls-style=flat] button.outlined:active:not([disabled]) {
  background: rgb(var(--accent-raw), 10%);
}
[ls-style=flat] ls-group[join] > :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button).elevated:first-child, [ls-style=flat] ls-group[join] > :is(button, input[type=button], input[type=submit], input[type=reset], .ls-button).elevated:not(:first-child, :last-child) {
  border-right: 1px solid var(--accent);
}
[ls-style=flat] ls-select:not(.plain) {
  overflow: hidden;
  padding: 0.2em 1.2em;
  padding-right: 2.4em;
  background: var(--elevate-1);
}
[ls-style=flat] ls-select:not(.pill) {
  border-radius: var(--br-small);
}
[ls-style=flat] ls-select:not(.plain):focus {
  outline: 2px solid var(--accent);
}
[ls-style=flat] ls-select:not(.plain).open {
  border-radius: var(--br) var(--br) 0 0 !important;
}
[ls-style=flat] .ls-tooltip-content {
  border: 0.05em solid rgba(0, 0, 0, 0.2196078431);
  box-shadow: inset 0 0.0625em 0 0 rgba(255, 255, 255, 0.1333333333);
  background: var(--ui-bg-1);
  border-radius: var(--br-small-inside);
}
[ls-style=flat] ls-menu {
  border-radius: var(--br-medium);
  box-shadow: 0px 3px 20px -4px rgba(0, 0, 0, 0.4117647059);
  padding: 4px 0;
}
[ls-style=flat] ls-option {
  border-radius: var(--br-medium-inside);
  margin: 3px 6px;
  padding: 5px 12px;
}
[ls-style=flat] .ls-checkbox > span, [ls-style=flat] .ls-radio > span {
  border: 2px solid var(--elevate-3);
  border-radius: var(--br-small-inside);
  transition: background 0.2s;
}
[ls-style=flat] .ls-radio > span {
  border-radius: 50%;
}
[ls-style=flat] :is(.ls-checkbox, .ls-radio) > span::before {
  content: "";
  border-radius: 50%;
  position: absolute;
  inset: -0.8em;
  transition: background 0.2s, transform 0.2s;
  transform: scale(0.8);
}
[ls-style=flat] .ls-radio > span::after {
  background: var(--accent);
  border-radius: 50%;
}
[ls-style=flat] :is(.ls-checkbox, .ls-radio) > input:disabled ~ span::before {
  display: none;
}
[ls-style=flat] :is(.ls-checkbox, .ls-radio):active > span::before {
  background: rgb(var(--accent-raw), 20%);
  transform: scale(1);
}
[ls-style=flat] .ls-checkbox > input:checked ~ span {
  background: var(--accent);
  border: none;
}
[ls-style=flat] .ls-radio > input:checked ~ span {
  border-color: var(--accent);
}
[ls-style=flat] :is(.ls-checkbox, .ls-radio) > input:disabled ~ span {
  opacity: 0.8;
  --elevate-3: var(--elevate-1);
  cursor: not-allowed;
}
[ls-style=flat] ls-modal {
  background: var(--ui);
  border-radius: 1rem;
  outline: 1px solid var(--transparent-overlay-strong);
  box-shadow: 0 10px 20px 0 rgba(0, 0, 0, 0.1333333333);
  flex-direction: column;
  overflow: hidden;
}
[ls-style=flat] ls-modal-body {
  padding: 1.4rem;
}
[ls-style=flat] ls-modal-footer > button {
  width: 100%;
  margin: 0;
  padding: 0;
  border-radius: 0;
  border-left: 1px solid var(--transparent-overlay);
  color: var(--accent);
  transition: background 0.1s;
  background: transparent !important;
}
[ls-style=flat] ls-modal-footer > button:active:not([disabled]) {
  background: var(--elevate-1) !important;
}

[ls-fluent], [ls-style=fluent] {
  --br: .4375em;
  --br-lg: .5625em;
}
[ls-fluent] :is(button, input[type=text], textarea):focus, [ls-fluent] [ls-selected], [ls-style=fluent] :is(button, input[type=text], textarea):focus, [ls-style=fluent] [ls-selected] {
  outline: 0.125em solid var(--accent-focus, var(--accent));
}
[ls-fluent] :is(button, input, textarea), [ls-style=fluent] :is(button, input, textarea) {
  color: var(--color-bg, #fff);
  background: var(--accent);
  border-radius: var(--br);
  border: 0.8px solid rgba(0, 0, 0, 0.4117647059);
  box-shadow: inset 0 0.0625em 0 0 rgba(255, 255, 255, 0.1607843137);
}
[ls-fluent] :is(input, textarea), [ls-style=fluent] :is(input, textarea) {
  box-shadow: inset 0 0.0625em 0 0 rgba(255, 255, 255, 0.1607843137), inset 0 -0.0625em 0 0 rgba(0, 0, 0, 0.1960784314);
}
[ls-fluent] button.elevated, [ls-style=fluent] button.elevated {
  background: var(--accent-light);
}
[ls-fluent] button.outlined, [ls-style=fluent] button.outlined {
  background: none;
  color: var(--accent-light);
  border: 1px solid var(--accent-light);
  transition-duration: 0.1s;
  transition-property: background;
}
[ls-fluent] :is(input[type=text], input:not([type=button]):not([type=submit]):not([type=color]):not([type=file]):not([type=reset]):not([type=image])), [ls-fluent] textarea, [ls-style=fluent] :is(input[type=text], input:not([type=button]):not([type=submit]):not([type=color]):not([type=file]):not([type=reset]):not([type=image])), [ls-style=fluent] textarea {
  color: var(--color);
  background: var(--ui-bg-0, var(--background));
}
[ls-fluent] button:active, [ls-style=fluent] button:active {
  background: var(--accent-dark);
  opacity: 0.8;
}
[ls-fluent] button:focus, [ls-fluent] button[ls-selected], [ls-style=fluent] button:focus, [ls-style=fluent] button[ls-selected] {
  box-shadow: none;
  background: var(--accent-dark);
}
[ls-fluent] .ls-tooltip-content, [ls-style=fluent] .ls-tooltip-content {
  border: 0.05em solid rgba(0, 0, 0, 0.2196078431);
  box-shadow: inset 0 0.0625em 0 0 rgba(255, 255, 255, 0.1333333333);
  background: var(--ui-bg-1);
  border-radius: var(--br);
}
[ls-fluent] ls-menu, [ls-style=fluent] ls-menu {
  border-radius: var(--br);
  box-shadow: 0px 20px 90px -40px #000, inset 0 -2px 0 0 rgba(0, 0, 0, 0.3333333333);
  padding: 0 0 4px;
  outline: 0.5px solid var(--ui-border);
}

[ls-style=sharp] {
  --br: 2px;
  --br-lg: 6px;

}
[ls-style=sharp] :is(button, input, select, textarea):focus {
  outline: 1px solid var(--accent-focus, var(--accent));
}
[ls-style=sharp] :is(button, input, select, textarea) {
  color: var(--color);
  background: var(--elevate-1);
  border-radius: var(--br);
}
[ls-style=sharp] :is(button, input, select, textarea):hover {
  opacity: 1;
}
[ls-style=sharp] :is(button, select):active {
  background: var(--ui-bg-1);
}
[ls-style=sharp] group[inputs] {
  box-shadow: 0 4px 40px -10px #000;
