.ls-automationgraph-container {
  position: relative;
  background: linear-gradient(var(--bg-1) 0, var(--bg-0) 100%);
  /* background: var(--bg-0); */
  display: inline-block;
}

.ls-automationgraph-container svg {
  stroke-width: 2px;
}

.ls-automationgraph-point-handle, .ls-automationgraph-curve-handle {
  position: absolute;
  transform: translate(-50%, -50%);
}

.ls-automationgraph-point-handle {
  outline: 2px solid var(--accent);
  background: var(--bg);
  cursor: grab;
  opacity: 0.4;
  transition: opacity 0.2s;
  padding: 4px;
  border-radius: 50%;
}

.ls-automationgraph-container:hover .ls-automationgraph-point-handle, .ls-automationgraph-container:hover .ls-automationgraph-curve-handle {
  opacity: 0.8;
}

.ls-automationgraph-curve-handle {
  width: 10px;
  height: 5px;
  background: var(--accent-transparent);
  outline: 1px solid var(--accent);
  cursor: pointer;
  border-radius: 20px;
  opacity: 0;
}

.ls-automationgraph-point-handle.active, .ls-automationgraph-point-handle:hover {
  outline: 2px solid var(--accent-light);
}

.ls-automationgraph-point-handle::after {
  content: "";
  opacity: 0;
  transform: rotate(0deg);
  scale: 1.2;
  transform-origin: center;
  border-radius: 50%;
  outline: 8px dashed var(--accent);
  outline-offset: -6px;
  position: absolute;
  inset: -10px;
  transition-duration: 0.2s;
  transition-timing-function: ease-in;
  animation: ls-rotate 2s linear infinite;
}

.ls-automationgraph-point-handle.active::after {
  opacity: 0.25;
  transform: rotate(0deg);
  scale: 1;
}

.ls-automationgraph-point-handle::before {
  content: "";
  position: absolute;
  top: 100%;
  height: var(--bottom);
  width: 2px;
  background: linear-gradient(rgb(var(--accent-raw), 25%) 0%, rgb(var(--accent-raw), 0) 100%);
  left: 50%;
  transform: translateX(-50%);
}

.ls-automationgraph-point-handle.active {
  cursor: none;
}

.ls-chart-vector {
  overflow: visible;
}

.ls-chart-container {
  position: relative;
}

.ls-chart-tooltip {
  position: absolute;
  opacity: 0;
  backdrop-filter: blur(20px);
  transition: opacity 0.2s;
  transform: translate(-50%, calc(-100% - 20px));
  z-index: 2;
  min-width: max-content;
  text-align: left;
  padding-top: 20px;
  overflow: hidden;
}

.ls-chart-tooltip-top {
  position: absolute;
  text-align: center;
  top: 0;
  left: 0;
  right: 0;
  background: rgba(0, 0, 0, 0.2666666667);
  padding: 8px 0;
}

.ls-chart-dot {
  display: inline-block;
  transform: scale(0.8);
  transform-origin: bottom;
  margin-right: 3px;
}

.ls-chart-dot,
.ls-chart-point {
  width: 1em;
  height: 1em;
  border-radius: 50%;
}

.ls-chart-point {
  position: absolute;
  opacity: 0;
  transition: 0.2s;
  z-index: 1;
  transform: translate(-50%, -50%);
  box-shadow: 0 5px 1em -0.2em rgba(0, 0, 0, 0.6), inset 0 0.1em 0 0 rgba(255, 255, 255, 0.2666666667);
}

.ls-chart-line {
  position: absolute;
  opacity: 0;
  bottom: 0;
  top: 0;
  width: 1px;
  border-left: 1px dashed rgba(255, 255, 255, 0.2);
  transition: 0.2s;
  z-index: 0;
}

.ls-chart-container:hover .ls-chart-point,
.ls-chart-container:hover .ls-chart-tooltip,
.ls-chart-container:hover .ls-chart-line {
  opacity: 1;
}

.ls-chart-point::after {
  position: absolute;
  content: "";
  top: 0.28em;
  bottom: 0.22em;
  left: 0.25em;
  right: 0.25em;
  background: rgba(255, 255, 255, 0.2);
  border-radius: 50%;
}

#ls-multiselect-mask {
  background: rgb(var(--accent-raw), 0.1);
  position: absolute;
  border-radius: 4px;
  z-index: 5000005;
  pointer-events: none;
  transition: opacity 0.2s;
}

.ls-listContainer {
  position: relative;
  overflow: hidden;
}

.ls-listItemContainer {
  line-height: var(--ls-list-lineHeight, 16px);
  position: sticky;
  height: 100%;
  overflow: visible;
  width: 100%;
  inset: 0;
}

.ls-listScrollContainer {
  overflow: overlay;
  position: absolute;
  inset: 0;
  --scrollbar-size: 0;
}

.ls-listScrollFilter {
  height: calc(100% + 800px);
}

.ls-listItem {
  height: var(--ls-list-lineHeight, 16px);
  width: 100%;
}

.ls-listItem.hidden {
  opacity: 0;
}

:root {
  --knob-size: 3em;
  --gear-size: 80%;
  --pointer-offset: 10%;
  --pointer-size: 5%;
}

ls-knob {
  display: inline-flex;
  position: relative;
  align-items: center;
  justify-content: center;
  width: var(--knob-size);
  height: var(--knob-size);
  border-radius: 50%;
}

ls-knob svg {
  overflow: visible;
}

ls-knob svg, .ls-knob-rotor {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
}

ls-knob * {
  pointer-events: none;
}

.ls-knob-stator {
  transition: all 0.3s;
  border-radius: 50%;
  position: absolute;
  width: var(--gear-size);
  height: var(--gear-size);
  filter: drop-shadow(0px 7px 0px rgba(0, 0, 0, 0.25));
}

.ls-knob-rotor::after {
  content: "";
  position: absolute;
  top: var(--pointer-offset);
  left: 50%;
  transform: translateX(-50%);
  background: var(--accent);
}

.ls-knob-glow .ls-knob-rotor::after {
  box-shadow: 0 0 5px 1px var(--accent);
}

[knob-pointer=dot] .ls-knob-rotor::after {
  padding: var(--pointer-size);
  border-radius: 50%;
}

[knob-pointer=line] .ls-knob-rotor::after {
  width: 2%;
  height: 30%;
  border-radius: 300px;
}

ls-knob[knob-preset=progress] {
  box-shadow: inset 0 0 0 var(--knob-stroke-width) var(--accent-transparent);
}

ls-knob[knob-preset=default] {
  background: linear-gradient(180deg, #333C41, #3E474C);
  outline: 1px solid rgba(0, 0, 0, 0.2666666667);
}

[knob-preset=flat] .ls-knob-arc-full {
  stroke: var(--accent-transparent);
  stroke-width: 7.5%;
  stroke-linecap: round;
}

[knob-preset=default] .ls-knob-stator {
  background: radial-gradient(100% 100% at 50% 17%, #515A5F 0%, #2C353A 100%);
  box-shadow: 0px -2px 0px 0px rgba(255, 255, 255, 0.08) inset, 0px 2px 0px 0px rgba(255, 255, 255, 0.25) inset;
}

[knob-preset=chrome] .ls-knob-stator {
  background: conic-gradient(#252c2f, #63696c, #35393c 130deg, #55595c, #2f3436, #66686b, 340deg, #252c2f) content-box;
  box-shadow: inset 0 0 0 1px rgba(255, 255, 255, 0.5333333333);
}

:is(ls-knob:hover, ls-knob.is-dragging) :is(.ls-knob-stator, .ls-knob-rotor) {
  transform: scale(90%);
}

:root {
  --patchbay-source-hitbox: 20px;
}

.ls-bay-node {
  position: absolute;
  border-radius: 0.625em;
  -webkit-user-select: none;
  user-select: none;
  display: flex;
}

.ls-bay-node:hover {
  background: rgba(255, 255, 255, 0.0666666667);
  outline: 1px solid rgba(255, 255, 255, 0.2509803922);
}

.ls-bay-node .node-content {
  padding: 0.625em 0.9625em;
  display: flex;
  align-items: center;
  z-index: -1;
  filter: drop-shadow(0 4px 2px rgba(0, 0, 0, 0.2666666667));
  flex-direction: column;
  align-items: center;
}

.ls-bay-node .node-content i {
  display: block;
  font-size: 2.5em;
}

.node-content[label]::after {
  content: attr(label);
  position: absolute;
  bottom: -17px;
}

.ls-bay-node .sources {
  display: flex;
  flex-direction: column;
  margin: -0.625em;
  flex-wrap: wrap;
  justify-content: center;
  pointer-events: none;
}

.ls-patch-source {
  pointer-events: none;
  background: var(--accent-dark);
  padding: 0.1875em;
  border-radius: 50%;
  border: 0.125em solid rgba(0, 0, 0, 0.5333333333);
  margin: 0.15625em;
}

.ls-patch-source-hitbox {
  pointer-events: all;
  padding: 0 var(--patchbay-source-hitbox);
  margin: 0 calc(var(--patchbay-source-hitbox) * -1);
}

.ls-patch-source-hitbox:first-child {
  padding: var(--patchbay-source-hitbox) var(--patchbay-source-hitbox) 0 var(--patchbay-source-hitbox);
  margin: calc(var(--patchbay-source-hitbox) * -1) calc(var(--patchbay-source-hitbox) * -1) 0 calc(var(--patchbay-source-hitbox) * -1);
}

.ls-patch-source-hitbox:last-child {
  padding: 0 var(--patchbay-source-hitbox) var(--patchbay-source-hitbox);
  margin: 0 calc(var(--patchbay-source-hitbox) * -1) calc(var(--patchbay-source-hitbox) * -1);
}

.ls-patch-source-hitbox:only-child {
  padding: var(--patchbay-source-hitbox);
  margin: calc(var(--patchbay-source-hitbox) * -1);
}

.ls-patch-source-hitbox:hover:not(.ls-drag-target) .ls-patch-source {
  background: var(--accent);
  padding: 0.25em;
  border: 0.0625em solid rgba(0, 0, 0, 0.5333333333);
  transform: scale(1.4);
}

.ls-patch-source-hitbox.ls-drag-target .ls-patch-source, .ls-patch-source-hitbox:focus .ls-patch-source {
  outline: 1px solid var(--accent);
}

.ls-bay-svg {
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  pointer-events: none;
  z-index: 5000000;
  overflow: visible;
}

.ls-workspace {
  position: relative;
  overflow: hidden;
  height: 100%;
  width: 100%;
}

.ls-workspace-scaler {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
}

.ls-workspace-content {
  position: absolute;
  width: 100%;
  height: 100%;
  overflow: visible;
  white-space: nowrap;
}

.ls-resize-bar-top, .ls-resize-bar-bottom {
  cursor: ns-resize;
  height: var(--resize-radius, 6px);
}

.ls-resize-bar-left, .ls-resize-bar-right {
  cursor: ew-resize;
  width: var(--resize-radius, 6px);
}

.ls-resize-bar-top {
  top: 0;
  left: 0;
  right: 0;
}

.ls-resize-bar-bottom {
  bottom: 0;
  left: 0;
  right: 0;
}

.ls-resize-bar-left {
  top: 0;
  left: 0;
  bottom: 0;
}

.ls-resize-bar-right {
  top: 0;
  bottom: 0;
  right: 0;
}

.ls-resize-bar {
  position: absolute;
  background: var(--transparent-overlay-strong);
}

.ls-resize-bar:hover {
  outline: 2px solid var(--transparent-overlay-strong);
}

.ls-resize-bar-topleft, .ls-resize-bar-topright, .ls-resize-bar-bottomleft, .ls-resize-bar-bottomright {
  width: var(--resize-corners-radius, 20px);
  height: var(--resize-corners-radius, 20px);
  z-index: 5;
  background: transparent;
}

.ls-resize-bar-topleft {
  top: 0;
  left: 0;
  cursor: nw-resize;
  transform: translate(-50%, -50%);
}

.ls-resize-bar-topright {
  top: 0;
  right: 0;
  cursor: ne-resize;
  transform: translate(50%, -50%);
}

.ls-resize-bar-bottomleft {
  bottom: 0;
  left: 0;
  cursor: sw-resize;
  transform: translate(-50%, 50%);
}

.ls-resize-bar-bottomright {
  bottom: 0;
  right: 0;
  cursor: se-resize;
  transform: translate(50%, 50%);
}

.ls-sheet-wrapper {
  display: flex;
  width: 100%;
  height: 100%;
  position: fixed;
  inset: 0;
  justify-content: center;
  pointer-events: none;
}

.ls-sheet-container {
  width: 45%;
  background: var(--ui);
  border-radius: 20px 20px 0 0;
  pointer-events: auto;
}
