                            // controlPointHandle.on("start", () => {
                            //     _initialY = M.y
                            //     _initialValue = current.values.points[i].curvature || 0
                            // })

                            // controlPointHandle.on("move", (x, y) => {
                            //     current.values.points[i].curvature = _initialValue + (y - _initialY);

                            //     updateHandle(true)
                            //     generatePoint(i)

                            //     draw()
                            // })

                            controlPointHandle.on("start", (x, y) => {
